import React, { useEffect, useRef } from 'react';
import ApexCharts from 'apexcharts';

// Your config object here

const GrowthChart: React.FC = () => {
  const growthChartEl = useRef<HTMLDivElement>(null);
  const growthChartInstance = useRef<ApexCharts | null>(null);

  useEffect(() => {
    // Your options here
    const growthChartOptions = {
      series: [78],
      labels: ['Growth'],
      chart: {
        height: 240,
        type: 'radialBar',
      },
      plotOptions: {
        radialBar: {
          size: 150,
          offsetY: 10,
          startAngle: -150,
          endAngle: 150,
          hollow: {
            size: '55%',
          },
          track: {
            background: 'red', // Replace 'cardColor' with your variable or color value
            strokeWidth: '100%',
          },
          dataLabels: {
            name: {
              offsetY: 15,
              color: 'blue', // Replace 'headingColor' with your variable or color value
              fontSize: '15px',
              fontWeight: '500',
              fontFamily: 'Public Sans',
            },
            value: {
              offsetY: -25,
              color: 'blue', // Replace 'headingColor' with your variable or color value
              fontSize: '22px',
              fontWeight: '500',
              fontFamily: 'Public Sans',
            },
          },
        },
      },
      colors: ['green'], // Replace 'config.colors.primary' with your variable or color value
      fill: {
        type: 'gradient',
        gradient: {
          shade: 'dark',
          shadeIntensity: 0.5,
          gradientToColors: ['green'], // Replace 'config.colors.primary' with your variable or color value
          inverseColors: true,
          opacityFrom: 1,
          opacityTo: 0.6,
          stops: [30, 70, 100],
        },
      },
      stroke: {
        dashArray: 5,
      },
      grid: {
        padding: {
          top: -35,
          bottom: -10,
        },
      },
      states: {
        hover: {
          filter: {
            type: 'none',
          },
        },
        active: {
          filter: {
            type: 'none',
          },
        },
      },
    };

    if (growthChartEl.current) {
      if (growthChartInstance.current) {
        growthChartInstance.current.destroy();
        growthChartInstance.current = null;
      }

      growthChartInstance.current = new ApexCharts(growthChartEl.current, growthChartOptions);
      growthChartInstance.current.render();
    }

    return () => {
      if (growthChartInstance.current) {
        growthChartInstance.current.destroy();
        growthChartInstance.current = null;
      }
    };
  }, []);

  return <div ref={growthChartEl} id="growthChart" />;
};

export default GrowthChart;
